import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Alert } from 'react-bootstrap';
import axios from 'axios';

function EmailVerification() {
    const navigate = useNavigate();
    const location = useLocation();
    const [alertVariant, setAlertVariant] = useState('success');
    const [alertMessage, setAlertMessage] = useState('Verifying email...');
    const [verificationInProgress, setVerificationInProgress] = useState(false);
    const [debounceTimeout, setDebounceTimeout] = useState(null);

    useEffect(() => {
        const handleVerification = async () => {
            const urlParams = new URLSearchParams(location.search);
            const code = urlParams.get('code');

            if (verificationInProgress) return; // Exit early if verification is already in progress

            setVerificationInProgress(true);

            try {
                const response = await axios.get(`http://localhost:8000/api/accounts/signup/verify/?code=${code}`);

                if (response.status === 200) {
                    setAlertVariant('success');
                    setAlertMessage('Email verified successfully.');

                    const timeout = setTimeout(() => {
                        navigate('/'); // Redirect to login after successful verification
                    }, 2000);

                    setDebounceTimeout(timeout);
                } else {
                    setAlertVariant('danger');
                    setAlertMessage('Email verification failed.');
                }
            } catch (error) {
                console.error('Error verifying email:', error);
                setAlertVariant('danger');
                setAlertMessage('Email verification failed.');
            } finally {
                setVerificationInProgress(false);
            }
        };

        if (debounceTimeout) {
            clearTimeout(debounceTimeout); // Clear previous timeout if it exists
        }

        const timeout = setTimeout(() => {
            handleVerification();
        }, 500); // Debounce time of 500ms

        setDebounceTimeout(timeout);

        return () => {
            if (debounceTimeout) {
                clearTimeout(debounceTimeout);
            }
        };
    }, [location, navigate, verificationInProgress, debounceTimeout]);

    return (
        <div>
            <Alert variant={alertVariant}>{alertMessage}</Alert>
        </div>
    );
}

export default EmailVerification;
